function App() {
  // Define a state variable to determine whether the user is logged in
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  return (
    <div>

      {isLoggedIn ? <Dashboard /> : <LoginForm setIsLoggedIn={setIsLoggedIn} />}
